def file_open():
    name = QtWidgets.QFileDialog.getOpenFileName(caption='Open File', directory=os.getcwd(),
                                                 filter="Text files (*.txt)")
    file = open(name[0], 'r')
    print(name[0])
    path=os.path.join(os.getcwd(), 'files\\users.txt')
    users = open(path, 'w')
    for line in file:
        users.write(line)
        #users.write("\n")
        # text = file.read()
        # self.contents.setText(text)
    users.close()


